var $=jQuery;class Millwood{constructor(t){var o=this;this.sitename=t,this.page={url:location.href,name:document.title,location:location},this.fn={init:function(){o.fn.force_full_width(),o.fn.get_custom_vars()},get_custom_vars:function(){let i=$("input#customvars").val().split("===");for(let t=0;t<i.length;t++){var n=i[t].split("-_-");o.page[n[0]]=n[1]}},force_full_width:function(){var t=$(window).width(),i=$("#primary.content-area").width(),n=-Math.abs((t-i)/2);$(".force-full-width").each(function(){$(this).css({width:t+"px",left:n+"px"})})}}}}$(document).ready(function(){var t=$("input#sitename").val();const i=new Millwood(t);i.fn.init(),$(window).on("resize",function(){i.fn.force_full_width()})});